import React,{useEffect,useState} from 'react'
import './Nav.css'
function Nav() {
    const [show, handleShow]= useState(false);
    useEffect(()=>{
        window.addEventListener("scroll", () =>{
            if(window.scrollY > 100){
                handleShow(true);
            }
            else handleShow(false);
        }
        );
        return () => {
            window.removeEventListener("scroll");
        };
    })
  return (
    <div className={"nav"}>
        <img className="nav__logo" src='https://upload.wikimedia.org/wikipedia/commons/7/7a/Logonetflix.png' atl='netflix logo'/>
        <img className="nav__avatar" src="https://upload.wikimedia.org/wikipedia/commons/0/0b/Netflix-avatar.png" alt="netflix avatar"/>
    </div>
  )
}

export default Nav
