import React,{useState,useEffect} from 'react'
import requests from './requests'
function Banner() {
    const [movies, setMovie]=useState([]);
    const [theMovie, setTheMovie]=useState([]);
    useEffect(()=>{
        fetchMovie(random)
        const random = Math.floor(Math.random() * movies.length());

    },[]);

    async function fetchMovie({index}){
        const response= await fetch(`https://api.themoviedb.org/3${requests.fetchNetflixOriginals}`);
        const data= response.json();
        setMovie(data.requests)
        setTheMovie(data.results[index]);
        return data.results[index];
    }
  return (
    <header>
        {
            <img src={theMovie.backdrop_path} alt={theMovie.name}/>
        }
    </header>
  )
}

export default Banner
