import './Row.css';
function Row({title, fetchrequest}){
    const baseURL="https://image.tmdb.org/t/p/original"
    const [movies,setMovies]=useState([]);
    useEffect(()=>{
        request();
    }, [fetchrequest]);

  async function request() {

        const reponse = await fetch(`https://api.themoviedb.org/3${fetchrequest}`);
        const data =await reponse.json();
        setMovies(data.results);
        return re
    }
 return(
    <div>
        <h1>{title}</h1>
        <div className="row">{movies.map( image =>(
            <img className="row__image" key={image.id} src={`${baseURL}${image.backdrop_path}`} alt={image.name}/>
        ))}</div>

    </div>
 ); 
}
export default Row;
